export const initialState = {
    user: null,
    playlists: [],
    playing: false,
    item: null
};

const reducer = (state, action) =>{ // state is the status of user, playlists, etc. action is changing one of those items
    console.log(action); 

    // Action -> type, [payload]

    switch(action.type){
        case 'SET_USER':
            return {
                ...state, 
                user: action.user
            }
        default:
            return state;
    }
} 

export default reducer;
